import React from 'react';
import {Button,Modal, ModalBody,ModalHeader,Row,Label,Col } from 'reactstrap';
import { Control, LocalForm, Errors } from 'react-redux-form';

class CommentForm extends React.Component{
constructor(props) {
    super(props);

    this.state = {
        isNavOpen: false,
        isModalOpen: false
        };

    this.toggleModal = this.toggleModal.bind(this);
    this.handleLogin = this.handleLogin.bind(this);
    
    }

      toggleModal() {
        this.setState({
          isModalOpen: !this.state.isModalOpen
        });
      }
      
      handleLogin(event) {
        this.toggleModal();
        alert("Username: " + this.username.value + " Password: " + this.password.value
            + " Remember: " + this.remember.checked);
        event.preventDefault();
    
    }

    render(){
        return (
            <div>
                <Button onClick={this.toggleModal} color="light"><span className="fas fa-pen"></span> Submit Comment</Button>
                <Modal isOpen={this.state.isModalOpen} toggle={this.toggleModal}>
                    <ModalHeader toggle={this.toggleModal}>Login</ModalHeader>
                    <ModalBody>
                        <LocalForm >
                            <Row className="form-group">
                                <Label htmlFor="rating">Rating</Label>
                                <Col md={10}>
                                    <Control.number model=".rating" id="rating" name="rating"
                                        placeholder="Rating"
                                        className="form-control"
                                     
                                         />
                                </Col>
                            </Row>
                            <Row className="form-group">
                                <Label htmlFor="username">Your Name</Label>
                                <Col md={10}>
                                    <Control.text model=".yourname" id="yourname" name="yourname"
                                        placeholder="Your Name"
                                        className="form-control"
                                     
                                         />
                                </Col>
                            </Row>
                            <Row className="form-group">
                                <Label htmlFor="username">Comment</Label>
                                <Col md={10}>
                                    <Control.textarea model=".comment" id="comment" name="comment"
                                        className="form-control"
                                  
                                         />
                                </Col>
                            </Row>
                            <Button type="submit" value="submit" color="primary">Submit</Button>
                        </LocalForm>
                    </ModalBody>
                </Modal>
            </div>
            )
    }
}

export default CommentForm
