import React from 'react';
import { Card, CardImg, CardText, CardBody,CardTitle,ListGroup,ListGroupItem,Breadcrumb, BreadcrumbItem } from 'reactstrap';
import { Link } from 'react-router-dom';
import CommentForm from './CommentForm';

   function RenderComments({res}){
        if (res != null){
           return(
               <div>
                   <h4>Comment</h4>
                   {
                       res.map(e=>{
                           console.log(e)
                           return(
                               <div key={e.id}><br />
                                   <div>{e.comment}</div><br />
                                   <div>--{e.author} , 
                                       {new Intl.DateTimeFormat('en-US', {year:'numeric', month:'short', day:'2-digit'})
                                       .format(new Date(Date.parse(e.date)))}
                                    </div>
                               </div>
                           )
                       })
                   }
               </div>
           )
          
        }
        else {
            return(
                <div></div>
            )
            
        }
    }

   function RenderDish({dish}) {
        return(
            <Card>
                <CardImg top src={dish.image} alt={dish.name} />
                <CardBody>
                    <CardTitle>{dish.name}</CardTitle>
                    <CardText>{dish.description}</CardText>
                </CardBody>
            </Card>
        );
    }

    const DishDetail = (props) => {
        console.log("DishDetail Component render is invoke")
        if(props.dish != null){
            return (
                <div className="container">
                    <div className="row">
                        <Breadcrumb>
        
                            <BreadcrumbItem><Link to="/menu">Menu</Link></BreadcrumbItem>
                            <BreadcrumbItem active>{props.dish.name}</BreadcrumbItem>
                        </Breadcrumb>
                        <div className="col-12">
                            <h3>{props.dish.name}</h3>
                            <hr />
                        </div>                
                    </div>
                    <div className="row">
                        <div className="col-12 col-md-5 m-1">
                            <RenderDish dish={props.dish} />
                        </div>
                        <div className="col-12 col-md-5 m-1">
                            <RenderComments res={props.comments} />
                        </div>
                    </div>
                    <div className="row">
                    <CommentForm />
                    </div>
                </div>
            );
        }
        else{
            return(
                <div></div>
            )
        }
      
    }
export default DishDetail
