import React from 'react';
import {Button,Modal, ModalBody,ModalHeader,Row,Label,Col,Form,FormGroup,Input } from 'reactstrap';
import { Control, LocalForm, Errors } from 'react-redux-form';

class CommentForm extends React.Component{
constructor(props) {
    super(props);

    this.state = {
        isModalOpen: false
        };

    this.toggleModal = this.toggleModal.bind(this);
    
    }

      toggleModal() {
        this.setState({
          isModalOpen: !this.state.isModalOpen
        });
      }
      handleSubmit(){
        this.setState({
            isModalOpen: false
          });
      }

    render(){
        return (
            <div>
                <Button onClick={this.toggleModal} color="light"><span className="fas fa-pen"></span> Submit Comment</Button>
                <Modal isOpen={this.state.isModalOpen} toggle={this.toggleModal}>
                    <ModalHeader>Submit Comment</ModalHeader>
                    <ModalBody>
                        <LocalForm onSubmit={() => this.handleSubmit()}>
                            <Row className="form-group">
                                <Col md={10}>
                                    <Label htmlFor="rating">Rating</Label>
                                    <Control.text model=".rating" id="rating" name="rating"
                                        className="form-control"
                                      
                                    />
                                </Col>
                            </Row>
                            <Row className="form-group">
                                <Col md={10}>
                                    <Label htmlFor="author">Your Name</Label>
                                    <Control.text model=".firstname" id="firstname" name="firstname"
                                        placeholder="First Name"
                                        className="form-control"
                                      
                                    />
                                </Col>
                            </Row>
                            <Row className="form-group">
                                <Col md={10}>
                                    <Label htmlFor="author">Comment</Label>
                                    <Control.text model=".firstname" id="firstname" name="firstname"
                                        placeholder="First Name"
                                        className="form-control"
                                     
                                    />
                                </Col>
                            </Row>
                            <Row className="form-group">
                                <Col md={{size:10, offset: 2}}>
                                    <Button type="submit" color="primary">Submit</Button>
                                </Col>
                            </Row>
                        </LocalForm>
                    </ModalBody>
                </Modal>
            </div>
            )
    }
}

export default CommentForm
